/*!50003 CREATE DEFINER=`root`@`%` PROCEDURE `lap_labkegiatan`(
        IN `_tglawal` DATE,
        IN `_tglakhir` DATE
    )
BEGIN
  set @dbmedikasoft := (select databasename from namadatabase where appname="medikasoft");
  SET @str := 
  	CONCAT("SELECT g.grup, 
        i.namaitem, i.iditemlab
        ,COUNT(td.iditemlab)jumlahperiksa
        #rawat jalan
        ,(select count(t1.notest)
            from lab_test t1
            left JOIN ",@dbmedikasoft,".rawatjalan rj ON t1.nodaftar=rj.faktur_rawatjalan
            inner join lab_testdetail td1 on td1.notest=t1.notest
            where t1.jenisrawat='rawat jalan'
            and (t1.tgltest between '",_tglawal,"' and '",_tglakhir,"')
            and td1.iditemlab=i.iditemlab
            and (rj.idprsh=1 or ISNULL(rj.faktur_rawatjalan))) rj_jmlumum
        ,(select count(rj.faktur_rawatjalan)
            from ",@dbmedikasoft,".rawatjalan rj
            inner JOIN lab_test t1 ON t1.nodaftar=rj.faktur_rawatjalan
            inner join lab_testdetail td1 on td1.notest=t1.notest
            where t1.jenisrawat='rawat jalan'
            and (t1.tgltest between '",_tglawal,"' and '",_tglakhir,"')
            and td1.iditemlab=i.iditemlab
            and rj.idprsh=6) rj_jmlbpjs
        ,(select count(rj.faktur_rawatjalan)
            from ",@dbmedikasoft,".rawatjalan rj
            inner JOIN lab_test t1 ON t1.nodaftar=rj.faktur_rawatjalan
            inner join lab_testdetail td1 on td1.notest=t1.notest
            where t1.jenisrawat='rawat jalan'
            and (t1.tgltest between '",_tglawal,"' and '",_tglakhir,"')
            and td1.iditemlab=i.iditemlab
            and rj.idprsh not in (1,6)) rj_jmllain
        #rawat inap
        ,(select count(ri.faktur_rawatinap)
            from ",@dbmedikasoft,".rawatinap ri
            inner JOIN lab_test t1 ON t1.nodaftar=ri.faktur_rawatinap
            inner join lab_testdetail td1 on td1.notest=t1.notest
            where t1.jenisrawat='rawat inap'
            and (t1.tgltest between '",_tglawal,"' and '",_tglakhir,"')
            and td1.iditemlab=i.iditemlab
            and ri.idprsh=1) ri_jmlumum
        ,(select count(ri.faktur_rawatinap)
            from ",@dbmedikasoft,".rawatinap ri
            inner JOIN lab_test t1 ON t1.nodaftar=ri.faktur_rawatinap
            inner join lab_testdetail td1 on td1.notest=t1.notest
            where t1.jenisrawat='rawat inap'
            and (t1.tgltest between '",_tglawal,"' and '",_tglakhir,"')
            and td1.iditemlab=i.iditemlab
            and ri.idprsh=6) ri_jmlbpjs
        ,(select count(ri.faktur_rawatinap)
            from ",@dbmedikasoft,".rawatinap ri
            inner JOIN lab_test t1 ON t1.nodaftar=ri.faktur_rawatinap
            inner join lab_testdetail td1 on td1.notest=t1.notest
            where t1.jenisrawat='rawat inap'
            and (t1.tgltest between '",_tglawal,"' and '",_tglakhir,"')
            and td1.iditemlab=i.iditemlab
            and ri.idprsh not in (1,6)) ri_jmllain
        #pasien luar
        ,(select count(t1.notest)
            from lab_test t1
            inner join lab_testdetail td1 on td1.notest=t1.notest
            where t1.jenisrawat='pasien luar'
            and (t1.tgltest between '",_tglawal,"' and '",_tglakhir,"')
            and td1.iditemlab=i.iditemlab) jmlpasienluar
        FROM lab_testdetail td                      
        inner JOIN lab_test t ON td.notest=t.notest 
        inner JOIN lab_itempemeriksaan i ON i.iditemlab=td.iditemlab
        LEFT JOIN lab_grup g ON g.idgrup=i.idgrup                   
        WHERE t.tgltest BETWEEN  '",_tglawal,"' and '",_tglakhir,"'

        GROUP BY iditemlab
        ORDER BY i.idgrup");
  PREPARE stmt3 FROM @str;
  EXECUTE stmt3;

  DEALLOCATE PREPARE stmt3;
END */$$
DELIMITER ;

