/* Procedure structure for procedure `sp_kartu_stok_taking` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_kartu_stok_taking` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`%` PROCEDURE `sp_kartu_stok_taking`( 
        IN nostoktaking_in VARCHAR(20) 
    )
BEGIN 
	DECLARE kodebarang1,nostoktaking1 VARCHAR(20); 
    DECLARE idgudang1,iduser1, 
            nourut1,idsatuan1,idsatuanasli INTEGER DEFAULT 0; 
    DECLARE tanggal1 DATETIME; 
    DECLARE stok1,stokmasuk1,stokkeluar1, 
            hargamasuk1,hargakeluar1, 
            hargataking1 DECIMAL(19,4) DEFAULT 0; 
    DECLARE exit_loop BOOLEAN;  
	DECLARE crs_update_stok CURSOR FOR  
		SELECT sd.kodebarang,sd.idgudang,s.nostoktaking, 
        	s.tanggal,sd.qty,s.iduser,sd.harga, 
            0, 
            sd.idsatuan     
    	FROM stoktaking_detail sd 
        LEFT JOIN stoktaking s ON s.nostoktaking=sd.nostoktaking 
    	WHERE (sd.nostoktaking=nostoktaking_in); 
     
    DECLARE CONTINUE HANDLER FOR NOT FOUND 
    SET exit_loop = TRUE; 
     
    OPEN crs_update_stok; 
     
    takingdetail_loop: LOOP 
    BEGIN 
      FETCH crs_update_stok INTO  
      	kodebarang1,idgudang1,nostoktaking1, 
        tanggal1,stok1,iduser1,hargataking1, 
        nourut1,idsatuan1; 
         
      IF exit_loop THEN 
      	CLOSE crs_update_stok; 
        LEAVE takingdetail_loop; 
      END IF; 
      	select coalesce((select max(ks.nourut) from kartu_stok ks 
    			where (ks.kodebarang=kodebarang1) and  
                      (ks.idgudang=idgudang1)),0) into nourut1; 
        SELECT idsatuan INTO idsatuanasli FROM barang WHERE kodebarang=kodebarang1; 
        IF idsatuan1<>idsatuanasli THEN 
        	SELECT (stok1*jumlahkonv*jumlahasli) INTO stok1 FROM barang_satuan 
            	WHERE kodebarang=kodebarang1  
                AND idsatuan_konv=idsatuan1;             
             
            set hargataking1=hargataking1/stok1; 
        END IF; 
         
       
        IF stok1>0 THEN 
          BEGIN 
        	SET stokmasuk1:=stok1; 
            SET hargamasuk1:=hargataking1; 
          END; 
          ELSE IF stok1<0 THEN 
            BEGIN 
        	  SET stokkeluar1:=stok1*-1; 
